package org.recipnet.site;

/**
 * A <code>RecipnetException</code> subclass for use when an inconsistency
 * is detected in the database, such as inconsistent data among db tables,
 * inconsistent values within a single row, bad (non-valid, non-parsable,
 * illegal) values in any field, <em>etc</em>.  This exception idicates
 * that something was read from the database that didn't make sense, wasn't
 * usable as expected, or contradicted other things we read from the
 * database.
 */
public class InconsistentDbException extends RecipnetException {
    
    /**
     * Creates a new <code>InconsistentDataException</code>
     */
    public  InconsistentDbException() {
        super();
    }
    
    /**
     * Creates a new <code>InconsistentDataException</code> with the specified
     * detail message
     *
     * @param message the detail message for this exception
     */
    public  InconsistentDbException(String message) {
        super(message);
    }
    
    /**
     * Creates a new <code>InconsistentDbException</code> with the specified
     * detail message and cause
     *
     * @param message a <code>String</code> containing the detail message for
     * this exception
     * @param cause the <code>Throwable</code> cause of this exception; may be
     * <code>null</code> if there is none or it is unknown
     */
    public  InconsistentDbException(String message, Throwable cause) {
        super(message, cause);
    }
    
}
