package org.recipnet.site.wrapper;

import org.recipnet.site.RecipnetException;

/**
 * A <code>RecipnetException</code> subclass for use when an exception must
 * be thrown as a result of an attempt to access a Reciprocal Net site
 * resource without proper authorization
 */
public class AuthorizationException extends RecipnetException {
    
    /**
     * Creates a new <code>AuthorizationException</code>
     */
    public  AuthorizationException() {
        this(null);
    }
    
    /**
     * Creates a new <code>AuthorizationException</code> with the specified
     * detail message
     *
     * @param message the detail message for this exception
     */
    public  AuthorizationException(String message) {
        super(message);
    }
    
}
